INSERT INTO `art_article` (`art_id`, `cat_id`, `uid`, `writer_id`, `art_source`, `art_title`, `art_keywords`, `art_summary`, `art_image`, `art_template`, `art_pages`, `art_categories`, `art_topics`, `art_elinks`, `art_forum`, `art_time_create`, `art_time_submit`, `art_time_publish`, `art_counter`, `art_rating`, `art_rates`, `art_comments`, `art_trackbacks`) VALUES (1, 2, 1, 1, 'XOOPS Official Site', 'All About XOOPS', 'XOOPS, PHP, MySQL, CMS, GNU, GPL, eXtensible Object Oriented Portal System, Dynamic system', 'XOOPS is a program that allows administrators to easily create dynamic websites with great content and many outstanding features. It is an ideal tool for developing small to large dynamic community websites, intra company portals, corporate portals, weblogs and much more. It can be installed on an Internet host with a PHP-capable web server (e.g., Apache) and a database (e.g., MySQL).', '', 'default', 'a:4:{i:0;i:1;i:1;i:2;i:2;i:3;i:3;i:4;}', 'a:2:{i:0;s:1:"1";i:1;s:1:"2";}', 'a:1:{i:0;i:1;}', 'http://xoops.org XOOPS official site', 3, 1131647857, 1131647875, 1131647875, 3, 0, 0, 0, 0),
